import java.util.PriorityQueue;
class KLargest {
    static int[] kLargest(int[] arr, int k) {
        PriorityQueue<Integer> pq = new PriorityQueue<>();
        for (int x : arr) {
            pq.add(x);
            if (pq.size() > k) pq.poll();
        }
        return pq.stream().mapToInt(Integer::intValue).toArray();
    }
}
