import java.util.*;
class FractionalKnapsack {
    static double knapsack(int[] weight, int[] value, int W) {
        int n = weight.length;
        Integer[] idx = new Integer[n];
        for (int i=0;i<n;i++) idx[i]=i;
        Arrays.sort(idx,(a,b)->Double.compare((double)value[b]/weight[b],(double)value[a]/weight[a]));
        double total=0;
        for (int i:idx) {
            if (W>=weight[i]) { total+=value[i]; W-=weight[i]; }
            else { total+=(double)value[i]*W/weight[i]; break; }
        }
        return total;
    }
}
