import java.util.*;
class JobSequencing {
    static int maxProfit(int[][] jobs) {
        Arrays.sort(jobs,(a,b)->b[1]-a[1]);
        int maxD=0; for (int[] j:jobs) maxD=Math.max(maxD,j[0]);
        boolean[] slot=new boolean[maxD+1]; int profit=0;
        for (int[] j:jobs) {
            for (int t=j[0];t>=1;t--) {
                if (!slot[t]) { slot[t]=true; profit+=j[1]; break; }
            }
        }
        return profit;
    }
}
