class Knapsack01 {
    static int knapsack(int[] wt, int[] val, int W) {
        int n=wt.length; int[][] dp=new int[n+1][W+1];
        for (int i=1;i<=n;i++) {
            for (int w=0;w<=W;w++) {
                dp[i][w]=dp[i-1][w];
                if (wt[i-1]<=w) dp[i][w]=Math.max(dp[i][w],dp[i-1][w-wt[i-1]]+val[i-1]);
            }
        }
        return dp[n][W];
    }
    public static void main(String[] args) {
        System.out.println(knapsack(new int[]{1,2,3},new int[]{6,10,12},5));
    }
}
