class SubsetEnum {
    static void allSubsets(int[] arr) {
        int n = arr.length;
        for (int mask = 0; mask < (1 << n); mask++) {
            System.out.print("{ ");
            for (int i = 0; i < n; i++)
                if ((mask >> i & 1) == 1) System.out.print(arr[i] + " ");
            System.out.println("}");
        }
    }
    public static void main(String[] args) { allSubsets(new int[]{1,2,3}); }
}
