class Trie {
    TrieNode root = new TrieNode();
    static class TrieNode { TrieNode[] children=new TrieNode[26]; boolean isEnd; }
    void insert(String w) {
        TrieNode n=root; for (char c:w.toCharArray()) { int i=c-'a'; if (n.children[i]==null) n.children[i]=new TrieNode(); n=n.children[i]; } n.isEnd=true;
    }
    boolean search(String w) {
        TrieNode n=root; for (char c:w.toCharArray()) { int i=c-'a'; if (n.children[i]==null) return false; n=n.children[i]; } return n.isEnd;
    }
    boolean startsWith(String p) {
        TrieNode n=root; for (char c:p.toCharArray()) { int i=c-'a'; if (n.children[i]==null) return false; n=n.children[i]; } return true;
    }
    public static void main(String[] args) {
        Trie t=new Trie(); t.insert("apple");
        System.out.println(t.search("apple")); System.out.println(t.startsWith("app"));
    }
}
