class DSU {
    int[] parent, rank;
    DSU(int n) { parent=new int[n]; rank=new int[n]; for (int i=0;i<n;i++) parent[i]=i; }
    int find(int x) { if (parent[x]!=x) parent[x]=find(parent[x]); return parent[x]; }
    void union(int a, int b) {
        int ra=find(a), rb=find(b); if (ra==rb) return;
        if (rank[ra]<rank[rb]) parent[ra]=rb;
        else if (rank[ra]>rank[rb]) parent[rb]=ra;
        else { parent[rb]=ra; rank[ra]++; }
    }
    boolean connected(int a, int b) { return find(a)==find(b); }
    public static void main(String[] args) {
        DSU dsu=new DSU(5); dsu.union(0,1); dsu.union(1,2);
        System.out.println(dsu.connected(0,2)); System.out.println(dsu.connected(0,3));
    }
}
