import java.util.HashMap;
class HashTableExample {
    public static void main(String[] args) {
        HashMap<String, Integer> map = new HashMap<>();
        map.put("Alice", 90);
        map.put("Bob", 85);
        System.out.println(map.get("Alice"));
        System.out.println(map.containsKey("Bob"));
        map.remove("Bob");
