class LinearProbingHash {
    int[] keys; int size;
    LinearProbingHash(int size) { this.size = size; keys = new int[size]; java.util.Arrays.fill(keys, -1); }
    void insert(int key) {
        int idx = key % size;
        while (keys[idx] != -1) idx = (idx + 1) % size;
        keys[idx] = key;
    }
    boolean search(int key) {
        int idx = key % size;
        while (keys[idx] != -1) {
            if (keys[idx] == key) return true;
            idx = (idx + 1) % size;
        }
        return false;
    }
}
