import java.util.*;
class BFS {
    static void bfs(List<List<Integer>> adj, int start, int V) {
        boolean[] visited = new boolean[V];
        Queue<Integer> q = new LinkedList<>();
        visited[start] = true;
        q.add(start);
        while (!q.isEmpty()) {
            int node = q.poll();
            System.out.print(node + " ");
            for (int nb : adj.get(node)) {
                if (!visited[nb]) { visited[nb] = true; q.add(nb); }
            }
        }
    }
}
