class BitApps {
    static void swap(int[] arr, int i, int j) {
        arr[i] ^= arr[j]; arr[j] ^= arr[i]; arr[i] ^= arr[j];
    }
    static int reverseBits(int n) {
        int result = 0;
        for (int i = 0; i < 32; i++) {
            result = (result << 1) | (n & 1);
            n >>= 1;
        }
        return result;
    }
    public static void main(String[] args) {
        int[] arr = {3, 7}; swap(arr, 0, 1);
        System.out.println(arr[0] + " " + arr[1]);
    }
}
